			["customText"] = "function()\n    local m=''\n    local tim=GetTime()\n    if  aura_env.Tick==86399 then aura_env.Tick=0 end--24\n    if  aura_env.Tick+0.7 > tim then \n        return aura_env.M\n    end        \n    --\n    local col='|c'..aura_env.col\n    if aura_env.config.time then\n        m=m..aura_env.time(false)\n    end    \n    local Bin, Bout, latencyHome, latencyWorld = GetNetStats()\n    \n    --fps\n    if aura_env.config.fps then\n        if aura_env.config.time then m=m..' ' end--\n        local fps = GetFramerate()\n        fps= string.format('%.0f',fps)           \n        m=m..fps..col..'fps|r'\n    end    \n    --\n    if aura_env.config.Wms or aura_env.config.Lms then\n        --World Latency\n        local ms=''\n        if aura_env.config.Wms then\n            ms=latencyWorld\n        end    \n        --home latency\n        if aura_env.config.Lms then\n            ms=ms..col..'(|r'..latencyHome..col..')|r'\n        end        \n        if m~='' then m=m..' ' end--\n        m=m..ms..col..'ms|r'\n    end\n    \n    --\n    if aura_env.config.Internet then\n        local inter\n        if  Bin>0.1 then\n            if Bin>1000 then \n                Bin=col..'v '..string.format('%.1f', Bin/1000)\n                inter=Bin .. col..'mb|r'\n            else\n                Bin=col..'v '..string.format('%.1f', Bin)\n                inter=Bin .. col..'kb|r'\n            end            \n        end        \n        if  Bout>0.1 then        \n            if inter then inter =inter..' ' end--\n            if Bout >1000 then\n                Bout=string.format('%.1f',Bout/1000)\n                inter=inter .. Bout .. col..'mb ^|r'\n            else\n                Bout=string.format('%.1f',Bout)\n                inter=inter .. Bout .. col..'kb ^|r'\n            end            \n        end\n        if inter then \n            if m~='' then m=m..'\\n' end--\n            m=m.. inter\n        end\n    end\n    \n    --\n    if aura_env.config.mem then \n        local i\n        local mem=0\n        for i=1,GetNumAddOns() do \n            mem = mem + GetAddOnMemoryUsage(i) \n        end\n        mem=string.format('%.2f',mem/1000)\n        if m~='' then m=m..'\\n' end--        \n        m=m..mem..col..'mb|r'\n    end\n    \n    -- client build info\n    local version, build, date, tocversion = GetBuildInfo()\n    local info=''\n    if aura_env.config.version then\n        info='v'..version \n    end\n    if aura_env.config.build then\n        if info~='' then info=info..' ' end\n        info=info..'('..build..')'\n    end\n    if aura_env.config.date then\n        if info~='' then info=info..' ' end\n        info=info..date\n    end\n    if aura_env.config.tocversion then\n        if info~='' then info=info..' ' end\n        info=info..'('..tocversion..')'\n    end\n    if info~='' then \n        if m~='' then if not aura_env.config.mem then m=m ..'\\n' else m=m..' ' end end--\n        m=m ..col ..info..'|r'\n    end\n    aura_env.Tick=tim    \n    \n    if aura_env.M~=m then aura_env.M=m end\n    return m\nend \n\n\n",
