			["customText"] = "function()\n    if aura_env.state\n    and aura_env.state.prediction\n    and aura_env.state.value\n    then\n        local c = aura_env.config\n        \n        local usable = aura_env.state.usable\n        local value = aura_env.state.value\n        local total = aura_env.state.total\n        local prediction = aura_env.state.prediction\n        \n        local sum = value + prediction\n        \n        local cutPrediction\n        local finalPrediction\n        local percentOvercap\n        \n        if c.overlay then\n            if not usable then --hide\n                aura_env.region:SetOverlayColor(1, 1, 1, 1, 0) \n            elseif sum > total + 50 then --overcap\n                local r, g, b, a = unpack(c.color2)\n                aura_env.region:SetOverlayColor(1, r, g, b, a)\n            else --default\n                local r, g, b, a = unpack(c.color1)\n                aura_env.region:SetOverlayColor(1, r, g, b, a)\n            end\n        end\n        \n        local overcap = sum > total + 50 and sum - total or 0 --IP overcap\n        if c.overcap and ((c.overcapShow == 1 and usable) or c.overcapShow == 2)then\n            percentOvercap = Round(overcap * 100 / prediction) --IP overcap percent\n            percentOvercap = percentOvercap == 0 and \"\" or percentOvercap > 100 and \"+100%\" or (\"+%s%%\"):format(percentOvercap)\n        end\n        \n        if c.prediction and ((c.predictionShow == 1 and usable) or c.predictionShow == 2) then\n            if overcap > 0 then\n                cutPrediction = Round(prediction - overcap)\n                overcap = Round(overcap)\n                if c.format == 1 or c.format == 2 then\n                    cutPrediction = aura_env.formatNumber(cutPrediction)\n                    overcap = aura_env.formatNumber(overcap)\n                end\n                finalPrediction= (\"+%s |cFFFF0000(%s)|r\"):format(cutPrediction, overcap)\n            else\n                cutPrediction = Round(prediction)\n                if c.format == 1 or c.format == 2 then\n                    cutPrediction = aura_env.formatNumber(cutPrediction)\n                end\n                finalPrediction = (\"+%s\"):format(cutPrediction)\n            end\n        end\n        \n        local check \n        if c.waste and value > total + 50 then\n            check = true\n        end\n        \n        if c.format == 1 or c.format == 2 then\n            value = aura_env.formatNumber(value)\n            total = aura_env.formatNumber(total)\n        end\n        \n        if check then\n            local r, g, b, a = unpack(c.color3)\n            local color = CreateColor(r, g, b, a)\n            value = color:WrapTextInColorCode(value)\n        end\n        \n        if c.display == 1 then\n            return (\"%s/%s\"):format(value, total), percentOvercap, finalPrediction\n        else\n            return value, percentOvercap, finalPrediction\n        end\n    end\nend\n\n--%c1 display text for current/total\n--%c2 display text for overlay\n--%c3 display text for prediction",
