						["names"] = {
						},
						["events"] = "CLEU:SPELL_AURA_APPLIED:SPELL_AURA_REFRESH:SPELL_AURA_APPLIED_DOSE:SPELL_AURA_REMOVED_DOSE:SPELL_AURA_REMOVED:SPELL_CAST_START",
						["spellIds"] = {
						},
						["custom"] = "function(allstates, event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, sourceGUID, sourceName, _, _, destGUID, destName, _, _, spellID, spellName, _, auraType = ...\n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" or subEvent == \"SPELL_AURA_REMOVED_DOSE\" or subEvent == \"SPELL_AURA_REFRESH\" then\n            --checks for buff/debuff applied to co-tanks.\n            if UnitIsPlayer(destName) and not UnitIsUnit(destName, \"player\") and UnitGroupRolesAssigned(destName) == \"TANK\" then\n                --grab our sound settings.\n                local enableSound = aura_env.config[\"enableSound\"]\n                local repeatSound = aura_env.config[\"repeatSound\"]\n                --grab our current spell toggle value.\n                local spellToggle = aura_env.config[tostring(spellID)]\n                --grab our current spell dropdown/slider values.\n                local spellDropdown = aura_env.config[spellID..\"dropdown\"]\n                local spellSlider   = aura_env.config[spellID..\"slider\"]\n                --check if spell toggle or spell dropdown/slider exist.\n                if spellToggle or ((spellDropdown or spellSlider) and enableSound == 1) then\n                    local _, icon, stacks, _, duration, expirationTime = WA_GetUnitAura(destName, spellID, aura_env.convert[auraType])\n                    if spellToggle then\n                        allstates[spellID..destGUID] = {\n                            changed = true,\n                            show = true,\n                            name = WA_ClassColorName(destName),\n                            icon = icon,\n                            stacks = stacks,\n                            progressType = \"timed\",\n                            expirationTime = expirationTime,\n                            duration = duration,\n                            spellID = spellID,\n                            autoHide = true,\n                        }\n                    end\n                    if (spellDropdown or spellSlider) and enableSound == 1 then\n                        if spellDropdown and spellDropdown == 1 then\n                            PlaySoundFile(aura_env.sound[aura_env.config[\"specifiedSound\"]], \"MASTER\")\n                        elseif stacks and stacks > 0 and spellSlider and spellSlider > 0 and spellSlider == stacks then\n                            PlaySoundFile(aura_env.sound[aura_env.config[\"specifiedSound\"]], \"MASTER\")\n                        elseif stacks and stacks > 0 and spellSlider and spellSlider > 0 and spellSlider < stacks and repeatSound then\n                            PlaySoundFile(aura_env.sound[aura_env.config[\"specifiedSound\"]], \"MASTER\")\n                        end\n                    end\n                end\n            end\n        elseif subEvent == \"SPELL_CAST_START\" and aura_env.config[tostring(spellID)] then\n            --checks for casts done by boss1-5, then if target has opposite debuff, it shows.\n            local debuffID = aura_env.associatedDebuff[spellID]\n            if debuffID then\n                for i = 1,5 do\n                    local unit = \"boss\"..i\n                    if UnitGUID(unit) == sourceGUID and not UnitIsUnit(unit..\"target\", \"player\") then\n                        local hasDebuff = select(10, WA_GetUnitDebuff(unit..\"target\", debuffID))\n                        if hasDebuff then\n                            local _, _, icon, startTimeMS, endTimeMS = UnitCastingInfo(unit)\n                            if icon and startTimeMS and endTimeMS then\n                                PlaySoundFile(aura_env.sound[aura_env.config[\"specifiedSound\"]], \"MASTER\")\n                                allstates[spellID..sourceGUID] = {\n                                    changed = true,\n                                    show = true,\n                                    name = \"Taunt\",\n                                    icon = icon,\n                                    progressType = \"timed\",\n                                    expirationTime = (endTimeMS / 1000),\n                                    duration = (endTimeMS / 1000) - GetTime(),\n                                    spellID = spellID,\n                                    autoHide = true,\n                                }\n                                break\n                            end\n                        end\n                    end\n                end\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" then\n            if allstates[spellID..destGUID] then\n                allstates[spellID..destGUID].changed = true\n                allstates[spellID..destGUID].show = false\n            end\n        end\n    end\n    return true\nend",
