			["customText"] = "function()\n    if aura_env.state then\n        local power = aura_env.state.status == \"\" and Round(aura_env.state.percent) .. \"%\" or aura_env.state.status\n        return aura_env.state.name, power\n    end\nend\n\n\n\n\n",
