				},
				["init"] = {
					["custom"] = "aura_env.healers = {}\nlocal c = aura_env.config\naura_env.inspect = LibStub:GetLibrary(\"LibGroupInSpecT-1.1\", true)\n\naura_env.classIcons = {\n    [\"DRUID\"] = 136041,\n    [\"MONK\"] = 608952,\n    [\"PALADIN\"] = 135920,\n    [\"PRIEST\"] = 237542, --holy icon as default\n    [\"SHAMAN\"] = 136052,\n}\n\naura_env.list = {\n    [29166] = \"HELPFUL\", --Innervate\n    [GetSpellInfo(257427)] = true,\n    [GetSpellInfo(130341)] = true,\n    [GetSpellInfo(160867)] = true,\n}\n\naura_env.GetUnitAura = function(unit)\n    local mana, ss, t1, t2\n    for i = 1, 40 do\n        local t = {UnitBuff(unit, i)}\n        local rep\n        if not mana and i < 4 then\n            rep = {UnitDebuff(unit, i)}\n        end\n        if not next(t) then break end\n        if rep and next(rep) and rep[10] == 252753 then\n            mana = true\n            t1 = rep\n        elseif not mana and (aura_env.list[t[1]] or aura_env.list[t[10]]) then\n            mana = true\n            t1 = t\n        end\n        if not ss and t[10] == 20707 then\n            ss = true\n            t2 = t\n        end\n    end\n    return mana and t1 or false, ss and t2 or false\nend\n\naura_env.findHealer = function()\n    local count = 0\n    for unit in WA_IterateGroupMembers() do\n        if UnitGroupRolesAssigned(unit) == \"HEALER\" then\n            count = count + 1\n            local info = nil\n            local GUID = UnitGUID(unit)\n            local self = false\n            if c.showSelf then\n                if GUID == WeakAuras.myGUID and unit~=\"player\" then self = true end\n            end\n            if aura_env.inspect and GUID then\n                info = aura_env.inspect:GetCachedInfo(GUID)\n            end\n            \n            local uid = self and \"player\" or unit\n            if info and info.spec_icon then --lib found\n                aura_env.healers[uid] = {\n                    unit, --unitID\n                    info.name, --UnitName\n                    info.class, --select(2, UnitClass())\n                    UnitPower(unit, Enum.PowerType.Mana), --UnitPower\n                    UnitPowerMax(unit, Enum.PowerType.Mana), --UnitPowerMax\n                    info.spec_icon, --specialization icon\n                    GUID, --UnitGUID\n                }\n            else\n                aura_env.healers[uid] = {\n                    unit, --unitID\n                    UnitName(unit), --UnitName\n                    select(2, UnitClass(unit)), --UnitClass\n                    UnitPower(unit, Enum.PowerType.Mana), --UnitPower\n                    UnitPowerMax(unit, Enum.PowerType.Mana), --UnitPowerMax\n                    aura_env.classIcons[select(2, UnitClass(unit))], --specialization icon\n                    GUID, --UnitGUID\n                }\n            end\n        end\n    end\n    \n    if count == 0 then\n        aura_env.healers = {}\n    else\n        WeakAuras.ScanEvents(\"HEALER_INFO\", true)\n    end\nend\n\nlocal sort1 = function(a,b) return a.state.percent < b.state.percent end\nlocal sort2 = function(a,b) return a.state.percent > b.state.percent end\n\naura_env.sortAndOffset = function(options)\n    local spacing = c.spacing or 2\n    local count = options and 1 or 0\n    local t = {}\n    \n    for k, v in pairs(WeakAuras.clones[aura_env.id]) do\n        if v.state and v.state.show and v.state.unitid then\n            table.insert(t, v)\n        end\n    end\n    \n    if c.sorting == 1 then\n        table.sort(t, sort1)\n    else\n        table.sort(t, sort2)\n    end\n    \n    for i, region in pairs(t) do\n        if region.toShow then\n            local xOffset\n            local yOffset\n            if (c.visible and region.state.status == \"FAR\") or --hide not visible\n            (c.dead and region.state.status == \"DEAD\") or --hide dead\n            (c.offline and region.state.status == \"OFFLINE\")\n            then --hide offline\n                region:SetAnchor(\"CENTER\" , WeakAuras.regions[aura_env.id].region, \"CENTER\")\n            else\n                if c.direction == 1 then\n                    xOffset = 0 - (region.width + spacing) * count\n                    yOffset = 0\n                elseif c.direction == 2 then\n                    xOffset = 0 + (region.width + spacing) * count\n                    yOffset = 0\n                elseif c.direction == 3 then\n                    xOffset = 0\n                    yOffset = 0 + (region.width + spacing) * count\n                elseif c.direction == 4 then\n                    xOffset = 0\n                    yOffset = 0 - (region.width + spacing) * count\n                end\n                \n                region:SetAnchor(\"CENTER\" , WeakAuras.regions[aura_env.id].region, \"CENTER\")\n                region:SetOffset(xOffset, yOffset)\n                \n                count = count + 1\n            end\n        end\n    end\nend",
