			["url"] = "https://wago.io/KtUU7No8j/13",
			["actions"] = {
				["start"] = {
				},
				["finish"] = {
				},
				["init"] = {
					["custom"] = "local seperator = \" \" .. aura_env.config[\"seperator\"] .. \" \"\nlocal showMainScore = aura_env.config[\"main_score\"]\nlocal shorten = aura_env.config[\"shorten_score\"]\nlocal searchScore = aura_env.config[\"search_score\"]\n\nBRIO = {}\nBRIO.LFG_DUNGEON_CATEGORY_ID = 2 -- can check with C_LFGList.GetCategoryInfo(categoryID)\nBRIO.LFG_MAX_ENTRY_NAME_LEN = 25 -- can check manually (+5 for title without voice icon)\nBRIO.NAME = \"\"\nBRIO.ShortenScore = function(score)\n    score = math.floor((score + 50) / 100)\n    return score / 10.0 .. \"k\"\nend\n\nBRIO.GetColorString = function(score)\n    local r, b, g = RaiderIO.GetScoreColor(score);\n    local rString = string.format(\"%x\", 255*r)\n    local bString = string.format(\"%x\", 255*b)\n    local gString = string.format(\"%x\", 255*g)\n    \n    if r*255 <= 15 then\n        rString = rString .. \"0\"\n    end\n    \n    if b*255 <= 15 then\n        bString = bString .. \"0\"\n    end\n    \n    if g*255 <= 15 then\n        gString = gString .. \"0\"\n    end\n    \n    local color = string.format(\"\\124cff%s%s%s\", rString, bString, gString)\n    return color\nend\n\n\nBRIO.GetPlayerRIO = function(fullname)\n    local fac = 1\n    if UnitFactionGroup(\"player\") == \"Horde\" then\n        fac = 2 \n    end\n    \n    local playerProfile = RaiderIO.GetProfile(fullname, fac);\n    if \n    playerProfile ~= nil and \n    playerProfile[\"mythicKeystoneProfile\"] ~= nil\n    then\n        return playerProfile[\"mythicKeystoneProfile\"].currentScore\n    end\n    \n    return 0;\nend\n\nBRIO.GetPlayerMainRIO = function(fullname)    \n    local fac = 1\n    if UnitFactionGroup(\"player\") == \"Horde\" then\n        fac = 2 \n    end\n    \n    local playerProfile = RaiderIO.GetProfile(fullname, fac);\n    if \n    playerProfile ~= nil and \n    playerProfile[\"mythicKeystoneProfile\"] ~= nil\n    then\n        return playerProfile[\"mythicKeystoneProfile\"].mainCurrentScore\n    end\n    \n    return 0;\nend\n\nBRIO.GetPlayerRIOString = function(name)\n    local score = BRIO.GetPlayerRIO(name)\n    local displayScore = score\n    if shorten and score >= 1000 then\n        displayScore = BRIO.ShortenScore(score)\n    end\n    \n    return BRIO.GetColorString(score) .. displayScore, score\nend\n\nBRIO.GetPlayerMainRIOString = function(name)\n    local mainScore = BRIO.GetPlayerMainRIO(name);\n    \n    local displayScore = mainScore\n    if shorten and mainScore >= 1000 then\n        displayScore = BRIO.ShortenScore(mainScore)\n    end\n    \n    return BRIO.GetColorString(mainScore) .. displayScore, mainScore;\nend\n\naura_env.UpdateApplicant = function(button, id)\n    button.InviteButton:SetWidth(25);\n    button.InviteButton:SetText(\"\");\n    button.InviteButton:SetNormalTexture(\"Interface\\\\Buttons\\\\UI-CheckBox-Check\")\nend\n\naura_env.UpdateApplicantMember = function(member, appID, memberIdx, status, pendingStatus)\n    local fullname, _ = C_LFGList.GetApplicantMemberInfo(appID, memberIdx)\n    BRIO.NAME = fullname\n    local scoreString = BRIO.GetPlayerRIOString(BRIO.NAME)\n    local mainScoreString, mainScore = BRIO.GetPlayerMainRIOString(BRIO.NAME)\n    \n    if mainScore > 0 and showMainScore then\n        scoreString = scoreString .. seperator .. mainScoreString\n    end\n    \n    member.ItemLevel:SetText(member.ItemLevel:GetText() .. seperator .. scoreString)\n    local _, relativeTo = member.ItemLevel:GetPoint(1)\n    member.ItemLevel:ClearAllPoints();\n    member.ItemLevel:SetPoint(\"CENTER\", relativeTo, \"RIGHT\", -3, 0);\nend\n\naura_env.SearchEntry_Update = function(group)\n    local result = C_LFGList.GetSearchResultInfo(group.resultID)\n    local categoryID = select(3, C_LFGList.GetActivityInfo(result.activityID))\n    if categoryID ~= BRIO.LFG_DUNGEON_CATEGORY_ID then return end\n    local score = BRIO.GetPlayerRIO(result.leaderName)\n    local scoreColor = BRIO.GetColorString(score);\n    local displayScore = score\n    \n    if shorten and score >= 1000 then\n        displayScore = BRIO.ShortenScore(score)\n    end\n    \n    local oldText = group.Name:GetText()\n    local newGrpText = string.format(\"%s[%s]\\124r %s\", scoreColor, displayScore, oldText);\n    group.Name:SetText(newGrpText)\nend\n\nhooksecurefunc(\"LFGListApplicationViewer_UpdateApplicant\", aura_env.UpdateApplicant)\nhooksecurefunc(\"LFGListApplicationViewer_UpdateApplicantMember\", aura_env.UpdateApplicantMember)\nif searchScore then\n    hooksecurefunc(\"LFGListSearchEntry_Update\", aura_env.SearchEntry_Update)\nend",
