						["custom"] = "function(allstates, event, ...)\n    --shows the name of your partner that you are linked to\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID = ...\n        if subEvent == \"SPELL_AURA_APPLIED\" and (spellID == 316542 or spellID == 316541) then\n            aura_env.counter = aura_env.counter + 1\n            aura_env.order[aura_env.counter] = WA_ClassColorName(destName)\n            if aura_env.myCounter then\n                local state = allstates[WeakAuras.myGUID]\n                if state then\n                    state.name = (state.spellID == 316542 and aura_env.order[aura_env.myCounter+1] or state.spellID == 316541 and aura_env.order[aura_env.myCounter-1]) or \"\"\n                    state.changed = true\n                end\n            end\n            if destGUID == WeakAuras.myGUID then\n                aura_env.myCounter = aura_env.counter\n                local _, _, _, _, duration, expirationTime = WA_GetUnitDebuff(destName, spellID)\n                allstates[destGUID] = {\n                    show = true,\n                    changed = true,\n                    name = (spellID == 316542 and aura_env.order[aura_env.myCounter+1] or spellID == 316541 and aura_env.order[aura_env.myCounter-1]) or \"\",\n                    progressType = \"timed\",\n                    duration = duration,\n                    expirationTime = expirationTime,\n                    autoHide = true,\n                    spellID = spellID\n                }\n            end\n        end\n        if subEvent == \"SPELL_AURA_REMOVED\" and (spellID == 316542 or spellID == 316541) \n        and allstates[destGUID] then\n            allstates[destGUID].show = false\n            allstates[destGUID].changed = true\n        end\n        if subEvent == \"SPELL_CAST_SUCCESS\" and spellID == 315927 then\n            aura_env.counter = 0\n            aura_env.order = {}\n            aura_env.myCounter = nil\n        end\n    end\n    if event == \"ENCOUNTER_START\" then\n        aura_env.counter = 0\n        aura_env.order = {}\n        aura_env.myCounter = nil\n    end\n    return true\nend",
