				["init"] = {
					["custom"] = "aura_env.spell = 190456\naura_env.surrender = IsPlayerSpell(202561)\n\nlocal c = aura_env.config\n\naura_env.formatNumber = function(value)\n    if not value or type(value) ~= \"number\" then return 1 end\n    if c.format == 1 then\n        if value > 999999999 then\n            value = string.format(\"%.2fB\", value/1000000000)\n        elseif value > 999999 then\n            value = string.format(\"%.2fM\", value/1000000)   \n        elseif value > 99999 then\n            value = string.format(\"%.0fK\", value/1000)\n        elseif value > 999 then\n            value = string.format(\"%.1fK\", value/1000)\n        elseif value <= 0 then\n            value = 0\n        end \n    elseif c.format == 2 then\n        if value > 999999999 then\n            value = (\"%d,%03d,%03d,%03d\"):format(floor(value/1000000000), floor(value/1000000)%1000, floor(value/1000)%1000, value%1000)\n        elseif value > 999999 then\n            value = (\"%d,%03d,%03d\"):format(floor(value/1000000), floor(value/1000)%1000, value%1000)\n        elseif value > 999 then\n            value = (\"%d,%03d\"):format(floor(value/1000), value%1000)\n        elseif value <= 0 then\n            value = 0\n        end\n    end\n    return value\nend\n\naura_env.values = {}\n\nlocal aura_env = aura_env\n\naura_env.location = function()\n    local i = 1\n    aura_env.IP = false\n    while true do\n        local name, rank, spellid = GetSpellBookItemName(i, BOOKTYPE_SPELL)\n        if not name then break end\n        if spellid == 190456 then\n            aura_env.IP = i\n            break\n        end\n        i = i + 1\n    end\nend\n\naura_env.location()\n\naura_env.IPvalue = function()\n    local tooltip = WeakAuras.GetHiddenTooltip()\n    if aura_env.IP then\n        tooltip:ClearLines()\n        tooltip:SetSpellBookItem(aura_env.IP, BOOKTYPE_SPELL)\n        wipe(aura_env.values)\n        local lines = {tooltip:GetRegions()}\n        for i, region in ipairs(lines) do\n            if region:GetObjectType() == \"FontString\" then\n                local text = region:GetText()\n                if text then\n                    for t in text:gmatch(\"(%d[%d%.,]*)\") do\n                        if (LARGE_NUMBER_SEPERATOR == \",\") then\n                            t = t:gsub(\",\", \"\");\n                        else\n                            t = t:gsub(\"%.\", \"\");\n                            t = t:gsub(\",\", \".\");\n                        end\n                        table.insert(aura_env.values, t)\n                    end\n                end\n            end\n        end\n    end\nend\n\naura_env.IPvalue()\n\naura_env.GetIPinfo = function()\n    local cur = UnitHealth(\"player\")\n    local max = UnitHealthMax(\"player\")\n    cur = cur and cur == 0 and 1 or cur\n    max = max and max == 0 and 1 or max\n    local percentHP = cur/max\n    local mod = aura_env.surrender and 1.4 or 1\n    if aura_env.surrender then\n        local bonus = 0.6 * (1 - percentHP)\n        mod = mod + bonus\n    end\n    aura_env.IPvalue()\n    local IP = aura_env.values[#aura_env.values] or 1\n    IP = IP * mod\n    local cap = floor(IP * 2)\n    return IP, cap\nend\n\naura_env.cur, aura_env.max = aura_env.GetIPinfo()",
