				["init"] = {
					["custom"] = "local aura_env = aura_env\nlocal config = aura_env.config\n\n-- Interpret user order (use default if order invalid)\nlocal order, checked = {}, {}\n\nfor i = 1, 6 do\n    local digit = string.sub(config.order, i, i) + 0\n    if (digit >= 1 and digit <= 6 and not checked[digit]) then\n        order[digit] = i\n        checked[digit] = true\n    else\n        order = nil\n        break\n    end\nend\naura_env.order = order or {1,2,3,4,5,6}\n\n\naura_env.spellIds = {193356, 199600, 193358, 193357, 199603, 193359}\n\n\naura_env.azCache = {}\naura_env.pandTable = {}\n\nfunction aura_env.SetPandemic()\n    aura_env.CPm = UnitPowerMax(\"player\", Enum.PowerType.ComboPoints)\n    aura_env.pandTable = {}\n    local pandTable = aura_env.pandTable\n    local desc = GetSpellDescription(193316) -- How else but to hardcode?\n    for val in desc:gmatch(\"(%d*) \" .. LOSS_OF_CONTROL_SECONDS) do\n        pandTable[#pandTable+1] = val * 0.3\n    end\n    pandTable[0] = 0\nend\n\nfunction aura_env.SetAzeriteCache()\n    aura_env.azCache = {}\n    local azCache = aura_env.azCache\n    for _, itemLocation in AzeriteUtil.EnumerateEquipedAzeriteEmpoweredItems() do\n        for _,tier in pairs(C_AzeriteEmpoweredItem.GetAllTierInfo(itemLocation)) do\n            for _,azeriteID in pairs(tier.azeritePowerIDs) do\n                if C_AzeriteEmpoweredItem.IsPowerSelected(itemLocation, azeriteID) then\n                    azCache[azeriteID] = azCache[azeriteID] and (azCache[azeriteID] + 1) or 1\n                    break\n                end\n            end\n        end\n    end\nend",
