			["triggers"] = {
				{
					["trigger"] = {
						["type"] = "custom",
						["subeventSuffix"] = "_CAST_START",
						["customVariables"] = "{\n    currentPandemic = {\n        display = \"Pandemic at current CP\",\n        type = \"bool\",\n        test = function(state, needle)\n            local exp = state.expirationTime\n            local pand = state.pandTable and state.pandTable[state.CPc]\n            if(exp and pand) then\n                local ret = pand >= (exp - GetTime()) \n                return needle == 1 == ret\n            end\n        end,\n        events = { \"FRAME_UPDATE\" }\n    },\n    fullPandemic = {\n        display = \"Pandemic at full CP\",\n        type = \"bool\",\n        test = function(state, needle)\n            local exp = state.expirationTime\n            local pand = state.pandTable and state.pandTable[state.CPm]\n            if(exp and pand) then\n                local ret = pand >= (exp - GetTime()) \n                return needle == 1 == ret\n            end\n        end,\n        events = { \"FRAME_UPDATE\" }\n    },\n    minusonePandemic = {\n        display = \"Pandemic at one less than full CP\",\n        type = \"bool\",\n        test = function(state, needle)\n            local exp = state.expirationTime\n            local pand = state.pandTable and state.pandTable[(state.CPm or 1) - 1]\n            if(exp and pand) then\n                local ret = pand >= (exp - GetTime()) \n                return needle == 1 == ret\n            end\n        end,\n        events = { \"FRAME_UPDATE\" }\n    },\n    checkcurrentPandemic = {\n        display = \"% of pandemic at current CP\",\n        type = \"number\",\n        test = function(state, needle)\n            local exp = state.expirationTime\n            local pand = state.pandTable and state.pandTable[state.CPc]\n            if(exp and pand) then\n                local ret = (exp - GetTime()) / pand\n                return ret\n            end\n        end,\n        events = { \"FRAME_UPDATE\" }\n    },\n    checkFullPandemic = {\n        display = \"% of pandemic at full CP\",\n        type = \"number\",\n        test = function(state, needle)\n            local exp = state.expirationTime\n            local pand = state.pandTable and state.pandTable[state.CPc]\n            if(exp and pand and pand ~= 0) then\n                local ret = (exp - GetTime()) / pand\n                return ret\n            end\n        end,\n        events = { \"FRAME_UPDATE\" }\n    },\n    buffactive = {\n        display = \"Buff active\",\n        type = \"bool\",\n    },\n    reroll = {\n        display = \"Reroll current buff\",\n        type = \"bool\",\n    },\n    fivebuff = {\n        display = \"Five buffs rolled\",\n        type = \"bool\",\n    },\n    \n}",
						["event"] = "Health",
						["subeventPrefix"] = "SPELL",
						["custom_type"] = "stateupdate",
						["spellIds"] = {
						},
						["custom"] = "function(allstates, event, unit, arg2)\n    if(event == \"OPTIONS\") then \n        local random = math.random\n        local config = aura_env.config\n        local alwaysone = false\n        for i = 1, 6 do\n            local reorder = aura_env.order[i]\n            local show = random(2) == 2 or (not alwaysone and i == 6)\n            alwaysone = alwaysone or show\n            allstates[reorder] = allstates[reorder] or {}\n            local state = allstates[reorder]\n            state.show = not config.hidebuffs and true or show\n            state.changed = true\n            state.index = reorder\n            state.progressType = \"static\"\n            state.text = config[\"buff\" .. i]\n            state.buffactive = show\n        end\n        return true\n    end\n    if(event == \"CONFIG_CLOSED\" or (event == \"UNIT_POWER_FREQUENT\" and arg2 == \"COMBO_POINTS\")) then\n        --print(arg1, arg2)\n        aura_env.CPc = UnitPower(\"player\", Enum.PowerType.ComboPoints)\n        for _, state in pairs(allstates) do\n            state.CPc = aura_env.CPc\n        end\n    elseif(event == \"PLAYER_TALENT_UPDATE\") then\n        aura_env.SetPandemic()\n    elseif(event == \"UNIT_AURA\" or event == \"CONFIG_CLOSED\") then\n        C_Timer.After(0.001, function() WeakAuras.ScanEvents(\"RTB_UNIT_AURA_DELAYED\") end)\n    elseif(event == \"RTB_UNIT_AURA_DELAYED\") then\n        local aura_env = aura_env\n        local config = aura_env.config\n        local roll = aura_env.roll or {}\n        roll[0] = 0\n        local reroll = true\n        \n        if(not (aura_env.pandTable and aura_env.pandTable[1])) then\n            aura_env.SetPandemic()\n        end\n        \n        for i = 1, 6 do\n            local t = WeakAuras.GetTriggerStateForTrigger(aura_env.id, i+1)\n            local tState = t and t[\"\"]\n            local reorder = aura_env.order[i]\n            \n            allstates[reorder] = allstates[reorder] or {}\n            local state = allstates[reorder]\n            state.show = not config.hidebuffs and true or tState and tState.show\n            state.changed = true\n            state.index = reorder\n            state.name = tState and tState.name\n            state.progressType = \"timed\"\n            state.duration = tState and tState.duration\n            state.total = nil\n            state.expirationTime = tState and tState.expirationTime\n            state.icon = tState and tState.icon --fix?\n            state.spellId = tState and tState.spellId or aura_env.spellIds[i]\n            state.text = config[\"buff\" .. i]\n            state.pandTable = aura_env.pandTable -- Conditions can't access aura_env?\n            state.CPm = aura_env.CPm -- Conditions can't access aura_env?\n            state.buffactive = tState and tState.show or false\n            roll[i] = tState and tState.show\n            roll[0] = roll[0] + (tState and tState.show and 1 or 0)\n        end\n        \n        -- Reroll logic as of June 2nd, 2019\n        \n        --During Blade Flurry if you have at least 1 rank of Keep Your Wits About You keep Grand Melee, broadside, Ruthless precision, and any 2 buff combination that is not Skull and Crossbones plus Buried treasure or True bearing.\n        \n        --2 buffs, or Ruthless Precision, or Grand melee. Ace up Your Sleeve or Deadshot you no longer keep solo Grand melee. With 2 or 3 snake eyes traits re-roll for 2 buffs, but do not reroll if snake eyes has 2(1 with broadside up) or more stacks left.\n        \n        --[[\n            1. Broadside\n            2. Buried Treasure\n            3. Grand Melee\n            4. Ruthless Precision\n            5. Skull and Crossbones\n            6. True Bearing\n            Keep Your Wits About You: 288979, 180\n            Ace Up Your Sleeve: 278676, 411\n            Deadshot: 272935, 129\n            Snake Eyes: 275746, 239\n        ]]--\n        \n        local fivebuff = roll[0] >= 5\n        local azCache = aura_env.azCache\n        local KYWAY = azCache[180] -- Keep Your Wits About You\n        local AUYS_DS = azCache[411] or azCache[129] -- Ace Up Your Sleeve or Deadshot\n        local SE = azCache[239] and azCache[239] >= 2 -- Snake Eyes\n        local set = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 9)\n        local ses = set and set[\"\"] and set[\"\"].stacks\n        local bft = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 8)\n        local bf = bft and bft[\"\"] and bft[\"\"].show\n        local dst = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 10)\n        local ds = dst and dst[\"\"] and dst[\"\"].show\n        \n        if(KYWAY and bf) then -- Blade Flurry section\n            if(roll[3] or (ds and roll[1]) or roll[4] or (roll[0] == 2 and not (roll[5] and (roll[2] or roll[6])))) then\n                reroll = false\n            end\n        else\n            if(SE) then -- Snake Eyes check\n                local stacks = ses or 0\n                if(roll[0] >= 2 or (stacks >= 2 or (stacks == 1 and roll[1]))) then\n                    reroll = false\n                end\n            elseif(roll[0] >= 2 or roll[4] or (roll[3] and not AUYS_DS)) then -- Standard reroll logic\n                reroll = false\n            end\n        end\n        for _, state in pairs(allstates) do\n            state.reroll = reroll\n            state.fivebuff = fivebuff\n        end\n        \n        WeakAuras.ScanEvents(\"RTB_REROLL\", reroll)\n        aura_env.roll = roll -- I would hope this is more efficient than recreating a table every run\n    end\n    if(event == \"AZERITE_ITEM_POWER_LEVEL_CHANGED\" or event == \"PLAYER_EQUIPMENT_CHANGED\" or\n    event == \"PLAYER_SPECIALIZATION_CHANGED\" or event == \"WA_DELAYED_PLAYER_ENTERING_WORLD\") then\n        aura_env.SetAzeriteCache()\n    end\n    return true\nend",
